/* 주간 일반구분(유형 06)제외 */ SELECT K1.ITEM_ID, K2.ITEM_NAME, K2.QTY, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX)),0) PLAN01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),SELL_BOX)),0) SELL01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX)),0) PRDT01, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX))),0),3)*100 RATE01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX)),0) PLAN02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),SELL_BOX)),0) SELL02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX)),0) PRDT02, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX))),0),3)*100 RATE02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX)),0) PLAN03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),SELL_BOX)),0) SELL03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX)),0) PRDT03, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX))),0),3)*100 RATE03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX)),0) PLAN04, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),SELL_BOX)),0) SELL04, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PRDT_BOX)),0) PRDT04, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX))),0),3)*100 RATE04, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),PLAN_BOX)),0) PLAN05, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),SELL_BOX)),0) SELL05, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),PRDT_BOX)),0) PRDT05, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*4,'D'),PLAN_BOX))),0),3)*100 RATE05, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*5,'D'),PLAN_BOX)),0) PLAN06, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*5,'D'),SELL_BOX)),0) SELL06, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*5,'D'),PRDT_BOX)),0) PRDT06, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*5,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*5,PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*5,PLAN_BOX))),0),3)*100 RATE06, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*6,PLAN_BOX)),0) PLAN07, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*6,SELL_BOX)),0) SELL07, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*6,PRDT_BOX)),0) PRDT07, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*6,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*6,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*6,'D'),PLAN_BOX))),0),3)*100 RATE07, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX)),0) PLAN08, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),SELL_BOX)),0) SELL08, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PRDT_BOX)),0) PRDT08, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX))),0),3)*100 RATE08, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX)),0) PLAN09, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),SELL_BOX)),0) SELL09, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PRDT_BOX)),0) PRDT09, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX))),0),3)*100 RATE09, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX)),0) PLAN10, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),SELL_BOX)),0) SELL10, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PRDT_BOX)),0) PRDT10, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX))),0),3)*100 RATE10, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),PLAN_BOX)),0) PLAN11, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),SELL_BOX)),0) SELL11, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),PRDT_BOX)),0) PRDT11, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PLAN_BOX))),0),3)*100 RATE11, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,PLAN_BOX)),0) PLAN12, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,SELL_BOX)),0) SELL12, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,PRDT_BOX)),0) PRDT12, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX))),0),3)*100 RATE12, NVL(SUM(PLAN_BOX),0) PLAN, NVL(SUM(SELL_BOX),0) SELL, NVL(SUM(PRDT_BOX),0) PRDT, ROUND(NVL(DECODE(SUM(PLAN_BOX),0,0, SUM(PRDT_BOX)/SUM(PLAN_BOX)),0),3)*100 RATE FROM ( /* 판매계획_월요일이 주 첫날_ -1 */ SELECT TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D') CNFM_DATE, S1.ITEM_ID ITEM_ID, SUM(NORMAL_QTY+PROM_QTY+SUPT_QTY+SAMP_QTY) PLAN_BOX, 0 SELL_BOX, 0 PRDT_BOX FROM NFOS_SALES_PLAN_WEEKLY S1, V_NFOS_PLAN_VERSION S2, ( SELECT ITEM_ID FROM ITEM_MST #if("$!{in_item_id}" != "") #if("$!{in_sel_gubn}" == "01") WHERE ITEM_ID = '$!{in_item_id}' --품목코드 #elseif("$!{in_sel_gubn}" == "05") WHERE (--품종 대중소 ('$!{in_item_name}' LIKE '대_%' AND SALES_CAT01 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '중_%' AND SALES_CAT02 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '소_%' AND SALES_CAT03 = '$!{in_item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE S1.YYYY = S2.YYYY AND S1.VERSION = S2.VERSION AND S1.YYYYMMDD = S2.YYYYMMDD AND S1.ITEM_ID = D2.ITEM_ID AND S1.YYYYMMDD BETWEEN REPLACE('$!{in_fr_date}','-','') AND REPLACE('$!{in_to_date}','-','') GROUP BY TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D'), S1.ITEM_ID UNION ALL /* 판매실적_월요일이 주 첫날_ -1 */ SELECT TRUNC(SALE_DATE-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, SUM(QTY) SELL_BOX, 0 PRDT_BOX FROM SALES_ACT D1, ( SELECT ITEM_ID FROM ITEM_MST #if("$!{in_item_id}" != "") #if("$!{in_sel_gubn}" == "01") WHERE ITEM_ID = '$!{in_item_id}' --품목코드 #elseif("$!{in_sel_gubn}" == "05") WHERE (--품종 대중소 ('$!{in_item_name}' LIKE '대_%' AND SALES_CAT01 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '중_%' AND SALES_CAT02 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '소_%' AND SALES_CAT03 = '$!{in_item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE SALE_DATE BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' #if("$!{in_act_type}" == "01") AND ACT_TYPE = '01' -- 일반 #elseif("$!{in_act_type}" == "02") AND ACT_TYPE = '02' -- 타계정 #end AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(SALE_DATE-1,'D'), D1.ITEM_ID UNION ALL SELECT TRUNC(START_DTTM-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, 0 SELL_BOX, SUM(COMPL_QTY) PRDT_BOX FROM V_ERP_PO_MES D1, ( SELECT ITEM_ID FROM ITEM_MST #if("$!{in_item_id}" != "") #if("$!{in_sel_gubn}" == "01") WHERE ITEM_ID = '$!{in_item_id}' --품목코드 #elseif("$!{in_sel_gubn}" == "05") WHERE (--품종 대중소 ('$!{in_item_name}' LIKE '대_%' AND SALES_CAT01 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '중_%' AND SALES_CAT02 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '소_%' AND SALES_CAT03 = '$!{in_item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE START_DTTM BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(START_DTTM-1,'D'), D1.ITEM_ID ) K1, ITEM_MST K2 WHERE K1.ITEM_ID = K2.ITEM_ID AND K2.ITYPE = 'FERT' GROUP BY K1.ITEM_ID, K2.ITEM_NAME, K2.QTY ORDER BY K1.ITEM_ID
#else
/* 유형으로 조회시 실적주기(주간) */ SELECT K1.ITEM_ID, K2.ITEM_NAME, K2.QTY, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX)),0) PLAN01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),SELL_BOX)),0) SELL01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX)),0) PRDT01, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX))),0),3)*100 RATE01, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX)),0) PLAN02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),SELL_BOX)),0) SELL02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX)),0) PRDT02, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX))),0),3)*100 RATE02, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX)),0) PLAN03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),SELL_BOX)),0) SELL03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX)),0) PRDT03, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX))),0),3)*100 RATE03, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX)),0) PLAN04, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),SELL_BOX)),0) SELL04, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PRDT_BOX)),0) PRDT04, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX)),0,0, S