1,SELL_BOX)),0) SELL12, NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,PRDT_BOX)),0) PRDT12, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX))),0),3)*100 RATE12, NVL(SUM(PLAN_BOX),0) PLAN, NVL(SUM(SELL_BOX),0) SELL, NVL(SUM(PRDT_BOX),0) PRDT, ROUND(NVL(DECODE(SUM(PLAN_BOX),0,0, SUM(PRDT_BOX)/SUM(PLAN_BOX)),0),3)*100 RATE FROM ( /* 판매계획_월요일이 주 첫날_ -1 */ SELECT TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D') CNFM_DATE, S1.ITEM_ID ITEM_ID, SUM(NORMAL_QTY+PROM_QTY+SUPT_QTY+SAMP_QTY) PLAN_BOX, 0 SELL_BOX, 0 PRDT_BOX FROM NFOS_SALES_PLAN_WEEKLY S1, V_NFOS_PLAN_VERSION S2, ( SELECT ITEM_ID FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE S1.YYYY = S2.YYYY AND S1.VERSION = S2.VERSION AND S1.YYYYMMDD = S2.YYYYMMDD AND S1.ITEM_ID = D2.ITEM_ID AND S1.YYYYMMDD BETWEEN REPLACE('$!{in_fr_date}','-','') AND REPLACE('$!{in_to_date}','-','') GROUP BY TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D'), S1.ITEM_ID UNION ALL /* 판매실적(타계정포함)_월요일이 주 첫날_ -1 */ SELECT TRUNC(SALE_DATE-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, SUM(QTY) SELL_BOX, 0 PRDT_BOX FROM SALES_ACT D1, ( SELECT ITEM_ID FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE SALE_DATE BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' #if("$!{in_act_type}" == "01") AND ACT_TYPE = '01' -- 일반 #elseif("$!{in_act_type}" == "02") AND ACT_TYPE = '02' -- 타계정 #end AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(SALE_DATE-1,'D'), D1.ITEM_ID UNION ALL SELECT TRUNC(START_DTTM-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, 0 SELL_BOX, SUM(COMPL_QTY) PRDT_BOX FROM V_ERP_PO_MES D1, ( SELECT ITEM_ID FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE START_DTTM BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(START_DTTM-1,'D'), D1.ITEM_ID ) K1, ITEM_MST K2 WHERE K1.ITEM_ID = K2.ITEM_ID AND K2.ITYPE = 'FERT' GROUP BY K1.ITEM_ID, K2.ITEM_NAME, K2.QTY UNION ALL /* 정품으로 박스를 환산한다 */ SELECT '', '정품 환산계', K3.QTY, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN01, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),SELL_BOX))/K3.QTY,0),0) SELL01, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT01, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*0,'D'),PLAN_BOX))),0),3)*100 RATE01, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN02, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),SELL_BOX))/K3.QTY,0),0) SELL02, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT02, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*1,'D'),PLAN_BOX))),0),3)*100 RATE02, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN03, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),SELL_BOX))/K3.QTY,0),0) SELL03, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT03, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*2,'D'),PLAN_BOX))),0),3)*100 RATE03, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*3,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN04, ROUND(N