LAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*6,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*6,'D'),PLAN_BOX))),0),3)*100 RATE07, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN08, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),SELL_BOX))/K3.QTY,0),0) SELL08, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT08, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*7,'D'),PLAN_BOX))),0),3)*100 RATE08, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN09, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),SELL_BOX))/K3.QTY,0),0) SELL09, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT09, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*8,'D'),PLAN_BOX))),0),3)*100 RATE09, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN10, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),SELL_BOX))/K3.QTY,0),0) SELL10, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT10, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*9,'D'),PLAN_BOX))),0),3)*100 RATE10, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),PLAN_BOX))/K3.QTY,0),0) PLAN11, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),SELL_BOX))/K3.QTY,0),0) SELL11, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*10,'D'),PRDT_BOX))/K3.QTY,0),0) PRDT11, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*10,PLAN_BOX))),0),3)*100 RATE11, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,PLAN_BOX))/K3.QTY,0),0) PLAN12, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,SELL_BOX))/K3.QTY,0),0) SELL12, ROUND(NVL(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}'),'D')+7*11,PRDT_BOX))/K3.QTY,0),0) PRDT12, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,TRUNC(TO_DATE('$!{in_fr_date}')+7*11,'D'),PLAN_BOX))),0),3)*100 RATE12, NVL(ROUND(SUM(PLAN_BOX/K3.QTY),0),0) PLAN, NVL(ROUND(SUM(SELL_BOX/K3.QTY),0),0) SELL, NVL(ROUND(SUM(PRDT_BOX/K3.QTY),0),0) PRDT, ROUND(NVL(DECODE(SUM(PLAN_BOX),0,0, SUM(PRDT_BOX)/SUM(PLAN_BOX)),0),3)*100 RATE FROM ( /* 판매계획_월요일이 주 첫날_ -1 */ SELECT TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D') CNFM_DATE, S1.ITEM_ID ITEM_ID, SUM(NORMAL_QTY+PROM_QTY+SUPT_QTY+SAMP_QTY)*D2.QTY PLAN_BOX, 0 SELL_BOX, 0 PRDT_BOX FROM NFOS_SALES_PLAN_WEEKLY S1, V_NFOS_PLAN_VERSION S2, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE S1.YYYY = S2.YYYY AND S1.VERSION = S2.VERSION AND S1.YYYYMMDD = S2.YYYYMMDD AND S1.ITEM_ID = D2.ITEM_ID AND S1.YYYYMMDD BETWEEN REPLACE('$!{in_fr_date}','-','') AND REPLACE('$!{in_to_date}','-','') GROUP BY TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD')-1,'D'), S1.ITEM_ID, D2.QTY UNION ALL /* 판매실적(타계정포함)_월요일이 주 첫날_ -1 */ SELECT TRUNC(SALE_DATE-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, SUM(D1.QTY)*D2.QTY SELL_BOX, 0 PRDT_BOX FROM SALES_ACT D1, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE SALE_DATE BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' #if("$!{in_act_type}" == "01") AND ACT_TYPE = '01' -- 일반 #elseif("$!{in_act_type}" == "02") AND ACT_TYPE = '02' -- 타계정 #end AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(SALE_DATE-1,'D'), D1.ITEM_ID, D2.QTY UNION ALL SELECT TRUNC(START_DTTM-1,'D') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, 0 SELL_BOX, SUM(COMPL_QTY)*D2.QTY PRDT_BOX FROM V_ERP_PO_MES D1, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE START_DTTM BETWEEN '$!{in_fr_date}' AND '$!{in_to_date}' AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(START_DTTM-1,'D'), D1.ITEM_ID, D2.QTY ) K1, ITEM_MST K2, ( SELECT TO_NUMBER(TRIM('$!{in_ea}')) QTY FROM DUAL WHERE TRIM('$!{in_ea}') IS NOT NULL UNION ALL SELECT QTY FROM ITEM_MST WHERE ITEM_ID = ( SELECT MIN(ITEM_ID) FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) AND TRIM('$!{in_ea}') IS NULL ) K3 WHERE K1.ITEM_ID = K2.ITEM_ID AND K2.ITYPE = 'FERT' GROUP BY K3.QTY ORDER BY ITEM_ID
