item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE S1.YYYY = S2.YYYY AND S1.VERSION = S2.VERSION AND S1.YYYYMMDD = S2.YYYYMMDD AND S1.ITEM_ID = D2.ITEM_ID AND S1.YYYYMMDD BETWEEN TO_CHAR(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),'YYYYMMDD') AND TO_CHAR(LAST_DAY(TO_DATE('$!{in_to_date}')),'YYYYMMDD') GROUP BY TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD'),'MM'), S1.ITEM_ID UNION ALL /* 판매실적(타계정포함) */ SELECT TRUNC(SALE_DATE,'MM') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, SUM(QTY) SELL_BOX, 0 PRDT_BOX FROM SALES_ACT D1, ( SELECT ITEM_ID FROM ITEM_MST #if("$!{in_item_id}" != "") #if("$!{in_sel_gubn}" == "01") WHERE ITEM_ID = '$!{in_item_id}' --품목코드 #elseif("$!{in_sel_gubn}" == "05") WHERE (--품종 대중소 ('$!{in_item_name}' LIKE '대_%' AND SALES_CAT01 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '중_%' AND SALES_CAT02 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '소_%' AND SALES_CAT03 = '$!{in_item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE SALE_DATE BETWEEN TRUNC(TO_DATE('$!{in_fr_date}','YYYY-MM-DD'),'MM') AND LAST_DAY(TO_DATE('$!{in_to_date}','YYYY-MM-DD')) #if("$!{in_act_type}" == "01") AND ACT_TYPE = '01' -- 일반 #elseif("$!{in_act_type}" == "02") AND ACT_TYPE = '02' -- 타계정 #end AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(SALE_DATE,'MM'), D1.ITEM_ID UNION ALL SELECT TRUNC(START_DTTM,'MM') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, 0 SELL_BOX, SUM(COMPL_QTY) PRDT_BOX FROM V_ERP_PO_MES D1, ( SELECT ITEM_ID FROM ITEM_MST #if("$!{in_item_id}" != "") #if("$!{in_sel_gubn}" == "01") WHERE ITEM_ID = '$!{in_item_id}' --품목코드 #elseif("$!{in_sel_gubn}" == "05") WHERE (--품종 대중소 ('$!{in_item_name}' LIKE '대_%' AND SALES_CAT01 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '중_%' AND SALES_CAT02 = '$!{in_item_id}') OR ('$!{in_item_name}' LIKE '소_%' AND SALES_CAT03 = '$!{in_item_id}') ) #else WHERE HR_TY1 LIKE '$!{in_item_id}'||'%' --사업코드,브랜드,대표코드 #end #end ) D2 WHERE START_DTTM BETWEEN TRUNC(TO_DATE('$!{in_fr_date}','YYYY-MM-DD'),'MM') AND LAST_DAY(TO_DATE('$!{in_to_date}','YYYY-MM-DD')) AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(START_DTTM,'MM'), D1.ITEM_ID ) K1, ITEM_MST K2 WHERE K1.ITEM_ID = K2.ITEM_ID AND K2.ITYPE = 'FERT' GROUP BY K1.ITEM_ID, K2.ITEM_NAME, K2.QTY ORDER BY K1.ITEM_ID
