DE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),10),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),10),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),10),PLAN_BOX))),0),3)*100 RATE11, ROUND(NVL(SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),PLAN_BOX))/K3.QTY,0),0) PLAN12, ROUND(NVL(SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),SELL_BOX))/K3.QTY,0),0) SELL12, ROUND(NVL(SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),PRDT_BOX))/K3.QTY,0),0) PRDT12, ROUND(NVL(DECODE(SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),PLAN_BOX)),0,0, SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),PRDT_BOX))/ SUM(DECODE(CNFM_DATE,ADD_MONTHS(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),11),PLAN_BOX))),0),3)*100 RATE12, NVL(ROUND(SUM(PLAN_BOX/K3.QTY),0),0) PLAN, NVL(ROUND(SUM(SELL_BOX/K3.QTY),0),0) SELL, NVL(ROUND(SUM(PRDT_BOX/K3.QTY),0),0) PRDT, ROUND(NVL(DECODE(SUM(PLAN_BOX),0,0, SUM(PRDT_BOX)/SUM(PLAN_BOX)),0),3)*100 RATE FROM ( /* 판매계획(봉수환산) */ SELECT TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD'),'MM') CNFM_DATE, S1.ITEM_ID ITEM_ID, SUM(NORMAL_QTY+PROM_QTY+SUPT_QTY+SAMP_QTY)*D2.QTY PLAN_BOX, 0 SELL_BOX, 0 PRDT_BOX FROM NFOS_SALES_PLAN_WEEKLY S1, V_NFOS_PLAN_VERSION S2, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE S1.YYYY = S2.YYYY AND S1.VERSION = S2.VERSION AND S1.YYYYMMDD = S2.YYYYMMDD AND S1.ITEM_ID = D2.ITEM_ID AND S1.YYYYMMDD BETWEEN TO_CHAR(TRUNC(TO_DATE('$!{in_fr_date}'),'MM'),'YYYYMMDD') AND TO_CHAR(LAST_DAY(TO_DATE('$!{in_to_date}')),'YYYYMMDD') GROUP BY TRUNC(TO_DATE(S1.YYYYMMDD,'YYYY-MM-DD'),'MM'), S1.ITEM_ID, D2.QTY UNION ALL /* 판매실적(타계정포함)(봉수환산) */ SELECT TRUNC(SALE_DATE,'MM') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, SUM(D1.QTY)*D2.QTY SELL_BOX, 0 PRDT_BOX FROM SALES_ACT D1, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE SALE_DATE BETWEEN TRUNC(TO_DATE('$!{in_fr_date}','YYYY-MM-DD'),'MM') AND LAST_DAY(TO_DATE('$!{in_to_date}','YYYY-MM-DD')) #if("$!{in_act_type}" == "01") AND ACT_TYPE = '01' -- 일반 #elseif("$!{in_act_type}" == "02") AND ACT_TYPE = '02' -- 타계정 #end AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(SALE_DATE,'MM'), D1.ITEM_ID, D2.QTY UNION ALL /* 생산실적(봉수환산) */ SELECT TRUNC(START_DTTM,'MM') CNFM_DATE, D1.ITEM_ID ITEM_ID, 0 PLAN_BOX, 0 SELL_BOX, SUM(COMPL_QTY)*D2.QTY PRDT_BOX FROM V_ERP_PO_MES D1, ( SELECT ITEM_ID, QTY FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) D2 WHERE START_DTTM BETWEEN TRUNC(TO_DATE('$!{in_fr_date}','YYYY-MM-DD'),'MM') AND LAST_DAY(TO_DATE('$!{in_to_date}','YYYY-MM-DD')) AND D1.ITEM_ID = D2.ITEM_ID GROUP BY TRUNC(START_DTTM,'MM'), D1.ITEM_ID, D2.QTY ) K1, ITEM_MST K2, ( SELECT TO_NUMBER(TRIM('$!{in_ea}')) QTY FROM DUAL WHERE TRIM('$!{in_ea}') IS NOT NULL UNION ALL SELECT QTY FROM ITEM_MST WHERE ITEM_ID = ( SELECT MIN(ITEM_ID) FROM ITEM_MST WHERE RECIPE_TYPE = '$!{in_item_id}' --유형코드 ) AND TRIM('$!{in_ea}') IS NULL ) K3 WHERE K1.ITEM_ID = K2.ITEM_ID AND K2.ITYPE = 'FERT' GROUP BY K3.QTY ORDER BY ITEM_ID
#end
#else
SELECT '' FROM DUAL WHERE 1=0
#end
